      <span class="token keyword">let</span> token <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">CancelToken</span><span class="token punctuation">(</span>c <span class="token operator">=></span> <span class="token punctuation">{</span>
        cancel <span class="token operator">=</span> c
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      cancel<span class="token operator">!</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>token<span class="token punctuation">.</span>reason<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toEqual</span><span class="token punctuation">(</span>expect<span class="token punctuation">.</span><span class="token function">any</span><span class="token punctuation">(</span>Cancel<span class="token punctuation">)</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>token<span class="token punctuation">.</span>reason<span class="token operator">!</span><span class="token punctuation">.</span>message<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toBe</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>

    <span class="token function">test</span><span class="token punctuation">(</span><span class="token string">'should has no side effect if call cancellation for multi times'</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">let</span> cancel<span class="token operator">:</span> Canceler
      <span class="token keyword">let</span> token <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">CancelToken</span><span class="token punctuation">(</span>c <span class="token operator">=></span> <span class="token punctuation">{</span>
        cancel <span class="token operator">=</span> c
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      cancel<span class="token operator">!</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
      cancel<span class="token operator">!</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>token<span class="token punctuation">.</span>reason<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toEqual</span><span class="token punctuation">(</span>expect<span class="token punctuation">.</span><span class="token function">any</span><span class="token punctuation">(</span>Cancel<span class="token punctuation">)</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>token<span class="token punctuation">.</span>reason<span class="token operator">!</span><span class="token punctuation">.</span>message<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toBe</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>

    <span class="token function">test</span><span class="token punctuation">(</span><span class="token string">'should returns undefined if cancellation has not been requested'</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">const</span> token <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">CancelToken</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
        <span class="token comment">// do nothing</span>
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>token<span class="token punctuation">.</span>reason<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toBeUndefined</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>
  <span class="token punctuation">}</span><span class="token punctuation">)</span>

  <span class="token function">describe</span><span class="token punctuation">(</span><span class="token string">'promise'</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
    <span class="token function">test</span><span class="token punctuation">(</span><span class="token string">'should returns a Promise that resolves when cancellation is requested'</span><span class="token punctuation">,</span> done <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">let</span> cancel<span class="token operator">:</span> Canceler
      <span class="token keyword">const</span> token <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">CancelToken</span><span class="token punctuation">(</span>c <span class="token operator">=></span> <span class="token punctuation">{</span>
        cancel <span class="token operator">=</span> c
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      token<span class="token punctuation">.</span>promise<span class="token punctuation">.</span><span class="token function">then</span><span class="token punctuation">(</span>value <span class="token operator">=></span> <span class="token punctuation">{</span>
        <span class="token function">expect</span><span class="token punctuation">(</span>value<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toEqual</span><span class="token punctuation">(</span>expect<span class="token punctuation">.</span><span class="token function">any</span><span class="token punctuation">(</span>Cancel<span class="token punctuation">)</span><span class="token punctuation">)</span>
        <span class="token function">expect</span><span class="token punctuation">(</span>value<span class="token punctuation">.</span>message<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toBe</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
        <span class="token function">done</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      cancel<span class="token operator">!</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>
  <span class="token punctuation">}</span><span class="token punctuation">)</span>

  <span class="token function">describe</span><span class="token punctuation">(</span><span class="token string">'throwIfRequested'</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
    <span class="token function">test</span><span class="token punctuation">(</span><span class="token string">'should throws if cancellation has been requested'</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
      <span class="token keyword">let</span> cancel<span class="token operator">:</span> Canceler
      <span class="token keyword">const</span> token <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">CancelToken</span><span class="token punctuation">(</span>c <span class="token operator">=></span> <span class="token punctuation">{</span>
        cancel <span class="token operator">=</span> c
      <span class="token punctuation">}</span><span class="token punctuation">)</span>
      cancel<span class="token operator">!</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
