          <span class="token function">fail</span><span class="token punctuation">(</span><span class="token string">'Expected throwIfRequested to throw a Cancel, but test threw '</span> <span class="token operator">+</span> thrown <span class="token operator">+</span> <span class="token string">'.'</span><span class="token punctuation">)</span>
        <span class="token punctuation">}</span>
