      <span class="token keyword">const</span> source <span class="token operator">=</span> CancelToken<span class="token punctuation">.</span><span class="token function">source</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>source<span class="token punctuation">.</span>token<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toEqual</span><span class="token punctuation">(</span>expect<span class="token punctuation">.</span><span class="token function">any</span><span class="token punctuation">(</span>CancelToken<span class="token punctuation">)</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>source<span class="token punctuation">.</span>cancel<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toEqual</span><span class="token punctuation">(</span>expect<span class="token punctuation">.</span><span class="token function">any</span><span class="token punctuation">(</span><span class="token builtin">Function</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
      <span class="token function">expect</span><span class="token punctuation">(</span>source<span class="token punctuation">.</span>token<span class="token punctuation">.</span>reason<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">toBeUndefined</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
      source<span class="token punctuation">.</span><span class="token function">cancel</span><span class="token punctuation">(</span><span class="token string">'Operation has been canceled.'</span><span class="token punctuation">)</span>
